00638 GRPBITMAPSEQUENCE* GRPBITMAPFILE::LoadSequence(XCHAR* namemask, int inisequence, int endsequence)
00639 {
00640   if(xpath.IsEmpty()) return NULL;
00641 
00642   GRPBITMAPSEQUENCE* bitmapsequence = new GRPBITMAPSEQUENCE();
00643   if(!bitmapsequence) return NULL;
00644 
00645   XSTRING namefile;
00646 
00647   //xpath.GetNamefileExt(namefile);
00648   //if(!namefile.IsEmpty()) return NULL;
00649 
00650   // The sequence is in individual files in a directory. Sequence numbers must be continuos
00651   int nfiles = inisequence;
00652 
00653   while((nfiles < endsequence)|| (endsequence < 0))
00654     {
00655       XPATH xpathtemp;
00656       bool  existfile = false;
00657 
00658       namefile.Format(namemask, nfiles);
00659       xpathtemp.Create(2, xpath.Get(), namefile.Get());
00660 
00661       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
00662       if(xfile)
00663         {
00664           existfile = xfile->Exist(xpathtemp);
00665           GEN_XFACTORY.Delete_File(xfile);
00666         }
00667 
00668       if(!existfile) break;
00669 
00670       GRPBITMAP* bitmap = Load(xpathtemp);
00671       if(!bitmap) break;
00672 
00673       bitmapsequence->AddFrame(bitmap);
00674 
00675       nfiles++;
00676     }
00677 
00678   return bitmapsequence;
