00160   int y = (wndheight - height) / 2;
00161 
00162   window =  XCreateWindow(display,RootWindow(display,screen),x ,y , width, height, 1,depth, 0 /*InputOutput*/, visual, CWBackPixel | CWBorderPixel | CWOverrideRedirect,&attributes);
00163   if(!window) return false;
00164 
00165   if(IsFullScreen())
00166     {
00167       Atom wm_state   = XInternAtom (display, "_NET_WM_STATE", true );
00168       Atom wm_fullscreen = XInternAtom (display, "_NET_WM_STATE_FULLSCREEN", true );
00169 
00170       XChangeProperty(display, window, wm_state, XA_ATOM, 32, PropModeReplace, (unsigned char *)&wm_fullscreen, 1);
00171 
00172       ScreenResolution(display, root, width, height, 60, false);
00173     }
00174 
00175   XMapWindow(display, window);
00176   XMoveWindow(display, window,x,y);
00177 
