00212 bool GRPLINUXSCREENX11::Update(GRPCANVAS* canvas)
00213 {
00214   if(!display) return false;
00215   if(!window)  return false;
00216   if(!canvas)  return false;
00217 
00218   GC gc = XCreateGC(display, window, 0, NULL);
00219   if(gc)
00220     {
00221       XSync(display,false);
00222 
00223       if(IsEqualSizeTo(canvas) == ISEQUAL)
00224         {
00225           XImage* image = CreateXImageFromBuffer(display, DefaultScreen(display), (XBYTE*)canvas->GetBuffer(), width, height);
00226           if(image)
00227             {
00228               XPutImage(display, window, gc, image, 0, 0, 0, 0, width, height);
00229               XDestroyImage(image);
00230             }
00231         }
00232         else
00233         {
00234           canvas->CopyBufferRenderToScreen(this);
00235 
00236           XImage* image = CreateXImageFromBuffer(display, DefaultScreen(display), (XBYTE*)buffer, width, height);
00237           if(image)
00238             {
00239               XPutImage(display, window, gc, image, 0, 0, 0, 0, width, height);
00240               XDestroyImage(image);
00241             }
00242         }
00243 
00244       XFreeGC(display, gc);
00245     }
00246 
00247   XFlush(display);
00248 
