00470 int GRPLINUXSCREENX11::ScreenResolution(Display* display, Window  root, int xsz, int ysz, int rate, int just_checking)
00471 {
00472   XRRScreenConfiguration*   xrr_config = 0;
00473   int                       event_base;
00474   int                       error_base;
00475   int                       ver_major;
00476   int                       ver_minor;
00477   int                       use_rate;
00478   Status                    result = -1;
00479 
00480   if(!display) return -1;
00481 
00482   // must check at runtime for the availability of the extension
00483   if(!XRRQueryExtension(display, &event_base, &error_base))
00484     {
00485         return -1;
00486     }
00487 
00488   XRRQueryVersion(display, &ver_major, &ver_minor);
00489 
00490   // we only heed the rate if we CAN actually use it (Xrandr >= 1.1) and the user actually cares about it (rate > 0)
00491   use_rate = ( rate > 0 ) && ( ( ver_major > 1 ) || (( ver_major == 1 ) && ( ver_minor >= 1 )));
00492 
00493   // this loop is only so that the whole thing will be repeated if someone else changes video mode between our query of the current information and the attempt to change it.
00494   do{
00495         XRRScreenSize*  ssizes;
00496         short*          rates;
00497         Rotation        rot;
00498         int             i;
00499         int             ssizes_count;
00500         int             rates_count;
00501         int             curr;
00502         int             res_idx = -1;
00503         Time timestamp, cfg_timestamp;
00504 
00505         if(xrr_config) XRRFreeScreenConfigInfo(xrr_config);
00506 
00507         if(!(xrr_config = XRRGetScreenInfo(display, root)))
00508           {
00509             //fgWarning("XRRGetScreenInfo failed");
00510             break;
00511           }
00512 
00513         ssizes    = XRRConfigSizes(xrr_config, &ssizes_count);
00514         curr      = XRRConfigCurrentConfiguration(xrr_config, &rot);
00515         timestamp = XRRConfigTimes(xrr_config, &cfg_timestamp);
00516 
00517         // if either of xsz or ysz are unspecified, use the current values
00518         if(xsz <= 0) xsz =  ssizes[curr].width;
00519         if(ysz <= 0) ysz =  ssizes[curr].height;
00520 
00521         if(xsz == ssizes[curr].width && ysz == ssizes[curr].height)
00522           {
00523             // no need to switch, we're already in the requested resolution
00524             res_idx = curr;
00525           }
00526          else
00527           {
00528             for(i=0; i<ssizes_count; i++)
00529               {
00530                 if(ssizes[i].width == xsz && ssizes[i].height == ysz)
00531                   {
00532                     res_idx = i;
00533                     break;  // found it
00534                   }
00535               }
00536           }
00537 
00538         if(res_idx == -1) break;  // no matching resolution
00539 
00540         #if ( RANDR_MAJOR > 1 ) || ( ( RANDR_MAJOR == 1 ) && ( RANDR_MINOR >= 1 ) )
00541         if(use_rate)
00542           {
00543             // for the selected resolution, let's find out if there is a matching refresh rate available.
00544             rates = XRRConfigRates(xrr_config, res_idx, &rates_count);
00545 
00546             for(i=0; i<rates_count; i++)
00547               {
00548                 if(rates[i] == rate)
00549                   {
00550                     break;
00551                   }
00552               }
00553 
00554             if(i == rates_count) break; // no matching rate
00555           }
00556         #endif
00557 
00558         if(just_checking)
00559           {
00560             result = 0;
00561             break;
00562           }
00563 
00564         #if ( RANDR_MAJOR > 1 ) || ( ( RANDR_MAJOR == 1 ) && ( RANDR_MINOR >= 1 ) )
00565         if(use_rate)
00566               result = XRRSetScreenConfigAndRate(display, xrr_config, root, res_idx, rot, rate, timestamp);
00567         else
00568         #endif
00569               result = XRRSetScreenConfig(display, xrr_config, root, res_idx, rot, timestamp);
00570 
00571     } while(result == RRSetConfigInvalidTime);
00572 
00573   if(xrr_config) XRRFreeScreenConfigInfo(xrr_config);
00574 
00575   if(result == 0) return 0;
00576 
00577   return -1;
00578 }
