00596 XImage* GRPLINUXSCREENX11::CreateXImageFromBuffer(Display* display, int screen, XBYTE* buffer, int width, int height)
00597 {
00598   XImage* image = NULL;
00599   int     depth;
00600   Visual* visual;
00601   double  rratio;
00602   double  gratio;
00603   double  bratio;
00604   int     outindex       = 0;
00605   int     numbufferbytes = (3 * (width * height));
00606 
00607   depth  = DefaultDepth(display,screen);
00608   visual = DefaultVisual(display,screen);
00609 
00610   rratio = visual->red_mask   / 255.0;
00611   gratio = visual->green_mask / 255.0;
00612   bratio = visual->blue_mask  / 255.0;
00613 
00614   if(depth >= 24)
00615     {
00616       int    numnewbufferbytes = (4 * (width * height));
00617       XDWORD* newbuffer        = (XDWORD*)malloc(numnewbufferbytes);
00618 
00619       for(int index=0;index<numbufferbytes;)
00620         {
00621           XDWORD b = (buffer[index++] * bratio);
00622           XDWORD g = (buffer[index++] * gratio);
00623           XDWORD r = (buffer[index++] * rratio);
00624 
00625           r &= visual->red_mask;
00626           g &= visual->green_mask;
00627           b &= visual->blue_mask;
00628 
00629           newbuffer[outindex] = r | g | b;
00630 
00631           ++outindex;
00632         }
00633 
00634       image = XCreateImage (display, CopyFromParent, depth,  ZPixmap, 0, (char *)newbuffer,width, height, 32, 0);
00635     }
00636     else
00637     {
00638       if(depth >= 15)
00639         {
00640           int   numnewbufferbytes = (2 * (width * height));
00641           XWORD* newbuffer          = (XWORD*)malloc(numnewbufferbytes);
00642 
00643           for(int index=0;index<numbufferbytes;)
00644             {
00645               XDWORD b = (buffer[index++] * bratio);
00646               XDWORD g = (buffer[index++] * gratio);
00647               XDWORD r = (buffer[index++] * rratio);
00648 
00649               r &= visual->red_mask;
00650               g &= visual->green_mask;
00651               b &= visual->blue_mask;
00652 
00653               newbuffer[outindex] = r | g | b;
00654 
00655               ++outindex;
00656             }
00657 
00658           image = XCreateImage(display, CopyFromParent, depth, ZPixmap, 0, (char*)newbuffer, width, height, 16, 0);
00659 
00660         }  else return NULL;
00661     }
00662 
00663   XInitImage(image);
00664 
00665   if(LSBFirst == GetByteOrder())
00666           image->byte_order = LSBFirst;
00667     else image->byte_order = MSBFirst;
00668 
00669   image->bitmap_bit_order = MSBFirst;
00670 
00671   return image;
00672 }
