00684 int GRPLINUXSCREENX11::GetByteOrder()
00685 {
00686   union
00687   {
00688     char c[sizeof(short)];
00689     short s;
00690 
00691   } order;
00692 
00693   order.s = 1;
00694   if((1 == order.c[0]))
00695     {
00696       return LSBFirst;
00697     }
00698    else
00699     {
00700       return MSBFirst;
00701     }
00702 }
