00629 GRPVIEWPORT* GRPSCREEN::GetViewport(XCHAR* ID)
00630 {
00631   for(XDWORD c=0; c<viewports.GetSize(); c++)
00632     {
00633       GRPVIEWPORT* viewport = viewports.Get(c);
00634       if(viewport) 
00635         {
00636           if(!viewport->GetID()->Compare(ID, true)) return viewport;
00637         }                    
00638     }
00639 
00640   return NULL;
00641 }
