00663 bool GRPSCREEN::CreateViewport(XCHAR* ID, float posx, float posy, float width, float height, XDWORD canvasposx, XDWORD canvasposy, XDWORD canvaswidth, XDWORD canvasheight)
00664 {
00665   GRPVIEWPORT* viewport = new GRPVIEWPORT();
00666   if(!viewport)  return false;
00667 
00668   viewport->GetID()->Set(ID);
00669   viewport->SetPosition(posx, posy);
00670   viewport->SetSize(width, height);
00671 
00672   GRPPROPERTIES canvasproperties;
00673 
00674   canvasproperties.CopyPropertysFrom(this);
00675 
00676   canvasproperties.SetPosition(canvasposx, canvasposy);
00677 
00678   float cwidth  = (float)canvaswidth;
00679   float cheight = (float)canvasheight;
00680 
00681   if(width  > cwidth)  cwidth  = width;
00682   if(height > cheight) cheight = height;
00683 
00684   canvasproperties.SetSize((XDWORD)cwidth, (XDWORD)cheight);
00685 
00686   if(viewport->CreateCanvas(canvasproperties))
00687     {
00688        viewport->GetCanvas()->GetScreenZone()->Set((int)posx, (int)posy, (int)(posx + width), (int)(posy + height));
00689        viewports.Add(viewport);
00690 
00691         // --------------------------------------------------------------------------------------------------
00692         // Main Canvas is selected if the name is the name of the main viewport or there is only one viewport.
00693   
00694        if((!viewport->GetID()->Compare(GRPVIEWPORT_ID_MAIN,true))  || viewports.GetSize() == 1)
00695           {
00696             index_maincanvas  = viewports.GetSize()-1;
00697             maincanvas        = viewport->GetCanvas();
00698           }
00699 
00700     } else return false;
00701 
00702   return true;
00703  }
