00715 bool GRPSCREEN::UpdateViewports()
00716 {
00717 
00718   #ifdef GRP_OPENGL_ACTIVE
00719 
00720 
00721   #else
00722     
00723     if(maincanvas)
00724       {        
00725         for(XDWORD c=0; c<viewports.GetSize(); c++)
00726           {
00727             if(index_maincanvas != c) 
00728               maincanvas->CopyBufferRenderFromViewport(viewports.Get(c));                               
00729           } 
00730            
00731         Update(maincanvas);      
00732       }
00733 
00734   #endif  
00735 
00736   if(framerate) framerate->AddNFrames();
00737 
00738   return true;
00739 }
