00137   
00138   XFILERIFF_LIST* strf_chunk = fileRIFF->GetChunk(__L("strf"), __L("strl"));
00139   if(!strf_chunk) return false;
00140   
00141   fileRIFF->GetChunkData(strf_chunk, (XBYTE*)&bitmapheader, sizedata);
00142 
