00157     { 
00158       GRPVIDEOFILEAVI_INDEXENTRY* indexentry = new GRPVIDEOFILEAVI_INDEXENTRY;
00159       if(!indexentry) return false;  
00160 
00161       if(!fileRIFF->GetFileBase()->Read((XBYTE*)indexentry, sizeof(GRPVIDEOFILEAVI_INDEXENTRY))) return false;
00162 
