00347 XBYTE* GRPVIDEOFILEAVI::GetDataFrame(XDWORD index_frame, XDWORD& sizeframe)
00348 {
00349   if(!frames_node)                      return NULL;
00350   if(index_frame >= propertys.nframes)  return NULL;
00351 
00352   XFILERIFF_LIST_NODE* frame_node = frames_node->GetChild(index_frame);
00353   if(!frame_node) return NULL;
00354 
00355   XFILERIFF_LIST* frame_list = frame_node->GetData();
00356   if(!frame_list) return NULL;
00357 
00358   XDWORD datasize = frame_list->GetSize();
00359 
00360   GRPVIDEOFILEAVI_INDEXENTRY* indexentry = indexentrys.Get(index_frame);
00361   if(!indexentry) return NULL;
00362     
00363   if(datasize != indexentry->chunklength) return NULL;  
00364     
00365   XBYTE* data = new XBYTE[datasize];
00366   if(!data) return NULL;
00367   
00368   if(!fileRIFF->GetFileBase()->SetPosition(frame_list->GetPositionFileData()))
00369     {
00370       delete [] data;
00371       return NULL;
00372     }
00373 
00374   if(!fileRIFF->GetFileBase()->Read(data, datasize))
00375     {
00376       delete [] data;
00377       return NULL;
00378     }
00379 
00380   sizeframe = datasize;
00381 
00382   return data;  
00383 }
