00398 bool GRPVIDEOFILEAVI::AddFrame(XBYTE* dataframe, XDWORD dataframesize)
00399 {
00400   if(!dataframe)      return false;
00401   if(!dataframesize)  return false;
00402 
00403   if(!movi_node)      return false;
00404 
00405 
00406   XDWORD framesize = dataframesize;
00407 
00408   //framesize += ((framesize % 2)?1:0);
