00410   //--------------------------------------------------------------------
00411   // AVI->movi->00dc
00412   
00413   XFILERIFF_LIST_NODE* frame_node = fileRIFF->CreateChunkNode(GRPVIDEOFILEAVI_TYPECHUNKFRAME, framesize);
00414   if(!frame_node) return false;
00415     
00416   fileRIFF->UpdateFilePosition(frame_node);    
00417   if(!fileRIFF->WriteListToFile(frame_node->GetData(), dataframe, framesize)) return false;  
00418 
00419   mainheader.totalframes++;
00420   movi_node->AddChild(frame_node);
00421   
00422 
00423   GRPVIDEOFILEAVI_INDEXENTRY* indexentry = new GRPVIDEOFILEAVI_INDEXENTRY();
00424   if(indexentry) 
00425     {
00426       indexentry->chunkID     = fileRIFF->GetTypeFromString(GRPVIDEOFILEAVI_TYPECHUNKFRAME);
00427       indexentry->flags       = 16;
00428       indexentry->chunklength = framesize;
00429       indexentry->chunkoffset = offsetforentrys;
00430 
00431       indexentrys.Add(indexentry);
00432 
00433       //offsetforentrys += (framesize + sizeof(GRPVIDEOFILEAVI_INDEXENTRY));
00434 
00435       offsetforentrys += (framesize + 8);
00436     }
00437  
00438   return true;
