00523 bool GRPVIDEOFILEAVI::CreateIndexofFrames()
00524 {
00525   if(!avi_node) return false;
00526 
00527   XDWORD sizeindex = sizeof(GRPVIDEOFILEAVI_INDEXENTRY) * mainheader.totalframes;
00528   
00529   XFILERIFF_LIST_NODE* index_node = fileRIFF->CreateChunkNode(__L("idx1"), sizeindex);
00530   if(!index_node) return false;
00531 
00532   fileRIFF->UpdateFilePosition(index_node);    
00533   if(!fileRIFF->WriteListToFile(index_node->GetData(), NULL, sizeindex)) return false;  
00534 
00535   avi_node->AddChild(index_node);
00536 
00537   for(XDWORD c=0; c<indexentrys.GetSize(); c++)
00538     {
00539       GRPVIDEOFILEAVI_INDEXENTRY* indexentry = indexentrys.Get(c);  
00540       if(indexentry)
00541         {
00542           if(!fileRIFF->GetFileBase()->Write((XBYTE*)indexentry, sizeof(GRPVIDEOFILEAVI_INDEXENTRY))) return false;
00543         }
00544     }
00545   
00546   return true;
00547 }
