00876 LRESULT CALLBACK GRPWINDOWSSCREEN::BaseWndProc(HWND hwnd, UINT msg, WPARAM wparam, LPARAM lparam)
00877 {
00878   switch(msg)
00879     {
00880       case WM_SYSCOMMAND      :  switch(wparam)
00881                                   {
00882                                     case SC_CLOSE         : break;
00883                                     case SC_SCREENSAVE    :
00884                                     case SC_MONITORPOWER  : return 0;
00885                                   }
00886                                 break;
00887 
00888       case WM_ENDSESSION      : break;
00889 
00890       case WM_CLOSE           : { GRPWINDOWSSCREEN* screen = GRPWINDOWSSCREEN::GetListScreens()->Get(hwnd);
00891                                   if(screen)
00892                                     {
00893                                       if(screen->IsBlockClose())
00894                                         {
00895                                           return 0;
00896                                         }
00897                                     }              
00898                                 }  
00899                                 break;
00900 
00901       case WM_DESTROY         : { int a=0;
00902                                   a++;
00903                                 }
00904                                 break;
00905 
00906       case WM_POWERBROADCAST  : //Computer is suspending
00907                                 if(wparam == PBT_APMSUSPEND)
00908                                   {
00909                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00910                                     if(mainprocwindows.GetAppMain())
00911                                       {
00912                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_SUSPEND);
00913                                       }
00914                                     #endif
00915                                   }
00916 
00917                                 break;
00918 
00919       case WM_QUERYENDSESSION : //Computer is shutting down
00920                                 if(lparam == 0)
00921                                   {
00922                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00923                                     if(mainprocwindows.GetAppMain())
00924                                       {
00925                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_REBOOT);
00926                                       }
00927                                     #endif
00928                                   }
00929 
00930                                 //User is logging off
00931                                 if((lparam & ENDSESSION_LOGOFF) == ENDSESSION_LOGOFF)
00932                                   {
00933                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00934                                     if(mainprocwindows.GetAppMain())
00935                                       {
00936                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_SESSION_LOGOFF);
00937                                       }
00938                                     #endif
00939                                   }
00940 
00941                                 break;
00942     }
00943 
00944   return DefWindowProc(hwnd, msg, wparam, lparam);
00945 }
