00384 bool INPCURSORMOTION::AddFromLine(INPCURSORMOTION_REDUCEDMODE reducedmode, int reducedfactor, int x1, int y1, int x2, int y2)
00385 {
00386   int x, xinc1, xinc2;
00387   int y, yinc1, yinc2;
00388   int i, deltax, deltay, npixels;
00389   int d, dinc1, dinc2;
00390 
00391   // Calculate deltax and deltay for initialisation
00392   deltax = abs(x2 - x1);
00393   deltay = abs(y2 - y1);
00394 
00395   // Initialize all vars based on which is the independent variable
00396   if(deltax>=deltay)
00397     {
00398       // x is independent variable }
00399       npixels   = deltax + 1;
00400       d         = (2 * deltay) - deltax;
00401       dinc1     = deltay >> 1;
00402       dinc2     = (deltay - deltax) >> 1;
00403       xinc1     = 1;
00404       xinc2     = 1;
00405       yinc1     = 0;
00406       yinc2     = 1;
00407     }
00408    else
00409     {
00410       // y is independent variable }
00411       npixels   = deltay + 1;
00412       d         = (2 * deltax) - deltay;
00413       dinc1     = deltax >> 1;
00414       dinc2     = (deltax - deltay) >> 1;
00415       xinc1     = 0;
00416       xinc2     = 1;
00417       yinc1     = 1;
00418       yinc2     = 1;
00419     }
00420 
00421   // Make sure x and y move in the right directions
00422   if(x1>x2)
00423     {
00424       xinc1 = -xinc1;
00425       xinc2 = -xinc2;
00426     }
00427 
00428   if(y1>y2)
00429     {
00430       yinc1 = -yinc1;
00431       yinc2 = -yinc2;
00432     }
00433 
00434   // Start drawing at
00435   x = x1;
00436   y = y1;
00437 
00438 
00439   int  steps    = CalculateReducedModeSteps(reducedmode,reducedfactor,npixels);
00440   int  incstep  = steps;
00441   bool addpoint = false;
00442 
00443   // Draw the pixels }
00444   for(i=0;i<npixels;i++)
00445     {
00446       if(incstep == steps)
00447         {
00448           addpoint = true;
00449           incstep  = 0;
00450         }
00451 
00452       if(addpoint)
00453             AddPoint((float)x, (float)y, 0);
00454        else incstep++;
00455 
00456       addpoint = false;
00457 
00458       if(d<0)
00459         {
00460           d = d + dinc1;
00461           x = x + xinc1;
00462           y = y + yinc1;
00463         }
00464        else
00465         {
00466           d = d + dinc2;
00467           x = x + xinc2;
00468           y = y + yinc2;
00469         }
00470     }
00471 
00472   return false;
00473 }
