00856 INPCURSORMOTION_DIR INPCURSORMOTION::GetDirectionByAngle(bool indetail)
00857 {  
00858   if(points.GetSize() < 2) return INPCURSORMOTION_DIR_UNKNOWN;
00859 
00860   float angle = GetAngle();
00861 
00862   if(indetail)
00863     {
00864       if((angle > 337.5f) || (angle <=  22.5f))  return INPCURSORMOTION_DIR_RIGHT;
00865       if((angle >  22.5f) && (angle <=  67.5f))  return INPCURSORMOTION_DIR_RIGHTUP;
00866       if((angle >  67.5f) && (angle <= 112.5f))  return INPCURSORMOTION_DIR_UP;
00867       if((angle > 112.5f) && (angle <= 157.5f))  return INPCURSORMOTION_DIR_LEFTUP;
00868       if((angle > 157.5f) && (angle <= 202.5f))  return INPCURSORMOTION_DIR_LEFT;
00869       if((angle > 202.5f) && (angle <= 247.5f))  return INPCURSORMOTION_DIR_LEFTDOWN;
00870       if((angle > 247.5f) && (angle <= 292.5f))  return INPCURSORMOTION_DIR_DOWN;
00871       if((angle > 292.5f) && (angle <= 337.5f))  return INPCURSORMOTION_DIR_RIGHTDOWN;
00872     }
00873    else
00874     {
00875       if((angle > 270) || (angle <=  45))        return INPCURSORMOTION_DIR_RIGHT;
00876       if((angle >  45) && (angle <= 135))        return INPCURSORMOTION_DIR_UP;
00877       if((angle > 135) && (angle <= 180))        return INPCURSORMOTION_DIR_LEFT;
00878       if((angle > 180) && (angle <= 270))        return INPCURSORMOTION_DIR_DOWN;
00879     }
00880 
00881   return INPCURSORMOTION_DIR_UNKNOWN;
00882 }
