00896 INPCURSORMOTION_DIR INPCURSORMOTION::GetDirectionByDifferential(XDWORD* differential)
00897 {
00898   INPCURSORMOTION_DIR dir = INPCURSORMOTION_DIR_UNKNOWN;
00899 
00900   if(differential) (*differential) = 0;
00901 
00902   if(points.GetSize() < 2) return dir;
00903 
00904   int max_up    = 0;
00905   int max_down  = 0;
00906   int max_right = 0;
00907   int max_left  = 0;
00908 
00909   int x_origin  = (int)points.Get(0)->GetX();
00910   int y_origin  = (int)points.Get(0)->GetY();
00911 
00912   for(XDWORD c=1; c<points.GetSize(); c++)
00913     {
00914       max_right = __MAX(((int)points.Get(c)->GetX() - x_origin),  max_right);
00915       max_left  = __MAX((x_origin - (int)points.Get(c)->GetX()),  max_left);
00916 
00917       max_up    = __MAX(((int)points.Get(c)->GetY() - y_origin),  max_up);
00918       max_down  = __MAX((y_origin - (int)points.Get(c)->GetY()),  max_down);     
00919     }
00920   
00921   int x   = __MAX(max_right, max_left);
00922   int y   = __MAX(max_up   , max_down);
00923 
00924   if(x > y)
00925     {
00926       if(differential) (*differential) = x;
00927 
00928       dir = INPCURSORMOTION_DIR_LEFT;
00929       if(max_right > max_left) dir = INPCURSORMOTION_DIR_RIGHT;
00930     }
00931    else
00932     {
00933       if(differential) (*differential) = y;
00934 
00935       dir = INPCURSORMOTION_DIR_DOWN;
00936       if(max_up > max_down) dir = INPCURSORMOTION_DIR_UP;
00937     }
00938  
00939   return dir;
00940 }
