01010 int INPCURSORMOTION::CalculateReducedModeSteps(INPCURSORMOTION_REDUCEDMODE mode,int factor,int npixels)
01011 {
01012   int steps = 0;
01013 
01014   switch(mode)
01015     {
01016       case INPCURSORMOTION_REDUCEDMODE_NONE            :                                 break;
01017       case INPCURSORMOTION_REDUCEDMODE_BYSTEPS         : steps = factor;                 break;
01018       case INPCURSORMOTION_REDUCEDMODE_NMAX            : steps = (npixels/factor);       break;
01019       case INPCURSORMOTION_REDUCEDMODE_NMAXPERCENT     : steps = (npixels*factor)/100;   break;
01020     }
01021 
01022   return steps;
01023 }
