00180                           button->SetState(INPBUTTON_STATE_RELEASED);
00181                         }
00182 
00183                       //XTRACE_PRINTCOLOR(1,__L("Keypres : code (%d) on : %s "), (buffer[c] & 0x7f), (button->IsPressed()?__L("Press"):__L("Release")));
00184                     }
00185                 }
00186             }
00187         }
00188     }
