00210                       if(button)
00211                         {
00212                           button->SetPressed(event.value!=0);
00213                           //XTRACE_PRINTCOLOR(1,__L("Keypres : code (%d) on : %s "), event.code, (button->IsPressed()?__L("Press"):__L("Release")));
00214 
00215                         } else continue;
00216 
00217                       switch(event.value)
00218                         {
00219                           case 0: button->SetState(INPBUTTON_STATE_RELEASED);
00220                                   break;
00221 
00222                           case 1: if(button->GetState() == INPBUTTON_STATE_PRESSED)
00223                                     {
00224                                       button->SetState(INPBUTTON_STATE_HOLD);
00225                                     }
00226                                    else
00227                                     {
00228                                       button->SetState(INPBUTTON_STATE_PRESSED);
00229                                     }
00230                                   break;
00231 
00232                           case 2: button->SetState(INPBUTTON_STATE_HOLD);
00233                                   break;
00234                         }
00235                     }
00236                 }
00237 
00238             } while(bytesread == eventsize);
