00383 bool INPLINUXDEVICEKEYBOARDDIRECT::CreateDevices()
00384 {
00385   INPLINUXFACTORY*     inpdevices = (INPLINUXFACTORY*)(&INPFACTORY::GetInstance());
00386   XVECTOR<INPLINUXDEVICEID*>  devicesID;
00387       
00388   if(inpdevices) inpdevices->GetDeviceHandlers(INPDEVICE_TYPE_KEYBOARD, devicesID);
00389 
00390   for(XDWORD c=0; c<devicesID.GetSize(); c++)
00391     {
00392       INPLINUXDEVICEID* deviceID = devicesID.Get(c);
00393       if(deviceID)
00394         {
00395           XSTRING event;
00396           int     filedescriptor = INPLINUXDEVICEID_INVALID;
00397      
00398           event.Format(__L("/dev/input/event%d"), deviceID->GetEventIndex());
00399 
00400           XBUFFER charstr;
00401           
00402           event.ConvertToASCII(charstr); 
00403           filedescriptor = open(charstr.GetPtrChar(), O_RDONLY | O_NONBLOCK);
00404           
00405           if(filedescriptor != -1)
00406             {
00407               char    keyboardOEMname[_MAXSTR];
00408               XSTRING namehardware;
00409 
00410               // we get the keyboard name
00411               memset(keyboardOEMname, 0, _MAXSTR);
00412   
00413               ioctl(filedescriptor, EVIOCGNAME(_MAXSTR), keyboardOEMname);
00414               namehardware.Set(keyboardOEMname);
00415                       
00416               deviceID->SetFileDescriptor(filedescriptor);
00417               //deviceID->GetName()->Set(namehardware);
00418 
00419               keyboards.Add(deviceID);
00420            }
00421         }
00422     }
00423 
00424   devicesID.DeleteAll();
00425   
00426   return keyboards.GetSize()?true:false;
00427 }
