00439 bool INPLINUXDEVICEKEYBOARDDIRECT::Old_CreateDevices()
00440 {
00441   struct termios newsettings;
00442 
00443   old_fd = Old_GetFD(NULL);
00444 
00445   if(ioctl(old_fd, KDGKBMODE, &old_keyboardmode))          return false;
00446 
00447   if(tcgetattr(old_fd, &old_termiosinitialsettings) == -1) return false;
00448   if(tcgetattr(old_fd, &newsettings) == -1)                return false;
00449 
00450   newsettings.c_lflag     &= ~(ICANON | ECHO | ISIG);
00451   newsettings.c_iflag      = 0;
00452   newsettings.c_cc[VMIN]   = INPLINUXDEVICEKEYBOARDDIRECT_OLDMAXBUFFER;
00453   newsettings.c_cc[VTIME]  = 1; /* 0.1 sec intercharacter timeout */
00454 
00455   if(tcsetattr(old_fd, TCSAFLUSH, &newsettings) == -1) return false;
00456 
00457   if(ioctl(old_fd, KDSKBMODE,  K_MEDIUMRAW)) return false;
00458 
00459   if(old_fd != -1)
00460     {
00461       INPLINUXDEVICEID* deviceID = NULL;
00462 
00463       deviceID = new INPLINUXDEVICEID();
00464       if(!deviceID) return false;
00465 
00466       deviceID->SetFileDescriptor(old_fd);
00467       keyboards.Add(deviceID);
00468 
00469       //XTRACE_PRINTCOLOR((deviceID?1:4),__L("Keyboard handle %d"), old_fd);
00470     }
00471 
00472   return true;
00473 }
