00485 bool INPLINUXDEVICEKEYBOARDDIRECT::Old_DeleteTerminalConfig()
00486 {
00487   if(old_fd == -1) return false;
00488 
00489   if(ioctl(old_fd, KDSKBMODE, old_keyboardmode))  return false;
00490 
00491   if(tcsetattr(old_fd, 0, &old_termiosinitialsettings) == -1) return false;
00492 
00493   close(old_fd);
00494 
00495   return true;
00496 }
