00510 int INPLINUXDEVICEKEYBOARDDIRECT::Old_IsConsole(int fd)
00511 {
00512   char arg;
00513 
00514   arg = 0;
00515 
00516   return (ioctl(fd, KDGKBTYPE, &arg) == 0 && ((arg == KB_101) || (arg == KB_84)));
00517 }
