00531 int INPLINUXDEVICEKEYBOARDDIRECT::Old_OpenConsole(const char *fnam)
00532 {
00533   int fd;
00534 
00535   /*
00536    * For ioctl purposes we only need some fd and permissions
00537    * do not matter. But setfont:activatemap() does a write.
00538    */
00539   fd = open(fnam, O_RDWR | O_NONBLOCK);
00540   if(fd < 0 && errno == EACCES) fd = open(fnam, O_WRONLY | O_NONBLOCK);
00541     if (fd < 0 && errno == EACCES) fd = open(fnam, O_RDONLY | O_NONBLOCK);
00542       if (fd < 0) return -1;
00543 
00544   if(!Old_IsConsole(fd))
00545     {
00546       close(fd);
00547       return -1;
00548     }
00549 
00550   return fd;
00551 }
