00565 int INPLINUXDEVICEKEYBOARDDIRECT::Old_GetFD(const char *fnam)
00566 {
00567   int fd;
00568 
00569   if (fnam)
00570     {
00571       fd = Old_OpenConsole(fnam);
00572       if (fd >= 0) return fd;
00573 
00574       return -1;
00575     }
00576 
00577   fd = Old_OpenConsole("/dev/tty");
00578   if (fd >= 0) return fd;
00579 
00580   fd = Old_OpenConsole("/dev/tty0");
00581   if (fd >= 0) return fd;
00582 
00583   fd = Old_OpenConsole("/dev/vc/0");
00584   if (fd >= 0) return fd;
00585 
00586   fd = Old_OpenConsole("/dev/console");
00587   if(fd >= 0) return fd;
00588     {
00589       for(fd = 0; fd < 3; fd++)
00590         {
00591           if (Old_IsConsole(fd))  return fd;
00592         }
00593     }
00594 
00595   return -1;
00596 }
