00126 bool INPLINUXDEVICEKEYBOARDX11::Update()
00127 {
00128   if((!created)||(!enabled))     return false;
00129 
00130   if(!grpscreenx11)              return false;
00131   if(!grpscreenx11->HasFocus())  return false;
00132 
00133   XEvent event;
00134 
00135   for(int e=0; e<this->buttons.GetSize(); e++)
00136     {
00137       INPBUTTON* button = buttons.FastGet(e);
00138 
00139       if(button->GetState() == INPBUTTON_STATE_RELEASED) button->SetState(INPBUTTON_STATE_UP);
00140       if(button->GetState() == INPBUTTON_STATE_PRESSED)  button->SetState(INPBUTTON_STATE_HOLD);
00141     }
00142 
00143   while(XCheckWindowEvent(grpscreenx11->GetDisplay(),(*grpscreenx11->GetWindow()),  KeyPressMask | KeyReleaseMask ,&event))
00144     {
00145       XKeyEvent* keyevent = (XKeyEvent *)&event;
00146 
00147       switch(event.type)
00148         {
00149           case KeyPress   : { long       keysym   = XLookupKeysym (keyevent, 0);
00150                               INPBUTTON* button   = GetButtonByCode(keysym);
00151 
00152                               if(!button)
00153                                 {
00154                                   KeySym low    = 0;
00155                                   KeySym upper  = 0;
00156 
00157                                   XConvertCase(keysym,&low,&upper);
00158 
00159                                   button        = GetButtonByCode(upper);
00160                                 }
00161 
00162                               if(!button) break;
00163 
00164                               if(button->GetState() == INPBUTTON_STATE_UP) button->SetState(INPBUTTON_STATE_PRESSED);
00165 
00166                               button->SetPressed(true);
00167                             }
00168                             break;
00169 
00170           case KeyRelease : { long       keysym   = XLookupKeysym (keyevent, 0);
00171                               INPBUTTON* button   = GetButtonByCode(keysym);
00172 
00173                               if(!button)
00174                                 {
00175                                   KeySym low    = 0;
00176                                   KeySym upper  = 0;
00177 
00178                                   XConvertCase(keysym,&low,&upper);
00179 
00180                                   button        = GetButtonByCode(upper);
00181                                 }
00182 
00183                               if(!button) break;
00184 
00185                               button->SetState(INPBUTTON_STATE_RELEASED);
00186 
00187                               button->SetPressed(false);
00188                             }
00189                             break;
00190         }
00191 
00192     }
00193   return true;
00194 }
