00208 bool INPLINUXDEVICEKEYBOARDX11::SetScreen(void* screenhandle)
00209 {
00210   grpscreenx11 = (GRPLINUXSCREENX11*)screenhandle;
00211   if(!grpscreenx11) return false;
00212 
00213   XSelectInput(grpscreenx11->GetDisplay(), (*grpscreenx11->GetWindow()), KeyPressMask | KeyReleaseMask);
00214 
00215   return true;
