00239                                       switch(button->GetState())
00240                                         {
00241                                           case INPBUTTON_STATE_UP       : (event.value==0) ? button->SetState(INPBUTTON_STATE_UP)       : button->SetState(INPBUTTON_STATE_PRESSED);  break;
