00243                                           case INPBUTTON_STATE_RELEASED :
00244                                           case INPBUTTON_STATE_HOLD     : (event.value==0) ? button->SetState(INPBUTTON_STATE_RELEASED) : button->SetState(INPBUTTON_STATE_PRESSED);  break;
