00438 bool INPLINUXDEVICEMOUSEDIRECT::UpdateMouseData()
00439 {
00440   if(!grpscreen) return false;
00441 
00442   for(int e=0;e<GetCursors()->GetSize();e++)
00443     {
00444       INPCURSOR* cursor = GetCursors()->Get(e);
00445       if(!cursor) continue;
00446 
00447       cursor->SetIsChanged(false);
00448 
00449       if((cursor->GetX() != mousex) || (cursor->GetY() != mousey))
00450         {
00451           cursor->Set(mousex, mousey);
00452           cursor->SetIsChanged(true);
00453         }
00454     }
00455 
00456   INPBUTTON* button = GetButton(INPBUTTON_ID_MOUSE_LEFT);
00457   if(button)
00458     {
00459       if(GetCursors()->Get(0)) GetCursors()->Get(0)->AddPointToMotion(button->IsPressed());
00460     }
00461 
00462   return true;
