00137 bool INPLINUXDEVICETOUCHSCREENDIRECT::Update()
00138 {
00139   if((!created)||(!enabled)) return false;
00140  
00141   struct input_event  event;
00142 
00143   int                 eventsize = sizeof(struct input_event);
00144   int                 bytesread = 0;
00145 
00146   //--------------- clean up old states
00147 
00148   INPBUTTON* button = GetButton(INPBUTTON_ID_TOUCHSCREEN);  
00149   if(button) 
00150     {
00151       button->SetPressed(false);
00152 
00153       touchscr_x = -1;
00154       touchscr_y = -1;
00155 
00156       UpdateTouchScreenData();
00157     }
00158 
00159   //--------------- process
00160 
00161   if(touchscr.GetSize() == 0)
00162     {
00163       OpenAllDevicesID();
00164     }
00165 
00166   for(XDWORD e=0; e<touchscr.GetSize(); e++)
00167     {
00168       while(true)
00169         {
00170           bytesread=0;
00171 
00172           memset(&event, 0, sizeof(event));
00173 
00174           errno = 0;
00175           bytesread = read(touchscr.Get(e)->GetFileDescriptor(), &event, sizeof(event)/*eventsize*/);
00176 
00177           // if device is diconnected we receive a ENODEV message (happens for example every time a touchscreen is poweredoff)
00178 
00179           if(bytesread == -1 && errno == ENODEV)
00180             {
00181               // we lost the handle to the event file, need to reclaim it
00182               touchscr.DeleteContents();
00183               OpenAllDevicesID();
00184             }
00185 
00186           if(bytesread==-1 && errno!=EAGAIN) break;
00187 
00188           if((bytesread < sizeof(struct input_event)) || (bytesread == -1))  break;
00189 
00190           if(bytesread == sizeof(event)/*eventsize*/)
00191             {
00192               switch(event.type)
00193                 {
00194                   case EV_ABS : switch(event.code)
00195                                   {
00196                                     case ABS_X      : { touchscr_x = event.value;                                                                                                              
00197                                                         //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Touch] x: %d"), touchscr_x);
00198 
00199                                                       }
00200                                                       break;
00201 
00202                                     case ABS_Y      : { touchscr_y = grpscreen->GetHeight() - event.value; 
00203                                                         //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Touch] y: %d"), touchscr_y);                                                                                                             
00204 
00205                                                         INPBUTTON* button = GetButton(INPBUTTON_ID_TOUCHSCREEN);
00206                                                         if(button) button->SetPressed(true);                                                        
00207                                                       } 
00208                                                       break;                               
00209                                    }
00210                                 break;
00211              
00212                       default : continue;
00213                 }
00214 
00215             } else break;
00216         }
00217     }
00218 
00219   UpdateTouchScreenData();
00220 
00221   return true;
00222 }
