00298             {
00299               char    mouseOEMname[_MAXSTR];
00300               XSTRING namehardware;
00301 
00302               // we get the keyboard name
00303               memset(mouseOEMname, 0, _MAXSTR);
00304   
00305               ioctl(filedescriptor, EVIOCGNAME(_MAXSTR), mouseOEMname);
00306               namehardware.Set(mouseOEMname);
00307                       
00308               deviceID->SetFileDescriptor(filedescriptor);
00309               //deviceID->GetName()->Set(namehardware);
00310 
00311               touchscr.Add(deviceID);
00312            }
00313         }
00314     }
00315 
00316   devicesID.DeleteAll();
00317 
00318   return touchscr.GetSize()?true:false;
