00108 bool INPWINDOWSDEVICEKEYBOARD::Update()
00109 {
00110   if((!created)||(!enabled)) return false;
00111 
00112   if(grpscreen)
00113     {
00114       if(grpscreen->GetHandle()!=GetForegroundWindow()) return false;
00115     }
00116 
00117   for(int c=0;c<(int)buttons.GetSize();c++)
00118     {
00119       INPBUTTON* button = (INPBUTTON*)buttons.Get(c);
00120 
00121       if(button)
00122         {
00123           if(GetKeyState(button->GetKeyCode()) & 0x80)
00124             {
00125               button->SetPressed(true);
00126             }
00127            else
00128             {
00129               button->SetPressed(false);
00130             }
00131         }
00132     }
00133 
00134   int n=buttons.GetSize();
00135   for(int c=0;c<n;c++)
00136     {
00137       INPBUTTON* button = (INPBUTTON*)buttons.FastGet(c);
00138       if(button)
00139         {
00140           SHORT state = GetAsyncKeyState(button->GetKeyCode());
00141           if(state & 0x8000) //key down
00142             {
00143               if(state & 0x01) //changed
00144                 {
00145                   if (button->GetState() != INPBUTTON_STATE_HOLD) button->SetState(INPBUTTON_STATE_PRESSED);
00146 
00147                 } else button->SetState(INPBUTTON_STATE_HOLD);
00148             }
00149            else
00150             {
00151               if(button->GetState() == INPBUTTON_STATE_HOLD || button->GetState() == INPBUTTON_STATE_PRESSED)
00152                 {
00153                   button->SetState(INPBUTTON_STATE_RELEASED);
00154 
00155                 } else button->SetState(INPBUTTON_STATE_UP);
00156             }
00157         }
00158     }
00159 
00160   return true;
00161 }
