00932 bool MAINPROCANDROID::GetPackageResourcePath(struct android_app* app, XSTRING& path)
00933 {
00934   JNIEnv* env;
00935   JavaVM* vm     = app->activity->vm;
00936   bool    status = false;
00937 
00938   vm->AttachCurrentThread(&env, NULL);
00939 
00940   jclass    clazz   = env->GetObjectClass(app->activity->clazz);
00941   jmethodID methodj = env->GetMethodID(clazz, "getPackageResourcePath", "()Ljava/lang/String;");
00942 
00943   jstring   jstr    = (jstring)env->CallObjectMethod(app->activity->clazz, methodj);
00944   if(jstr != NULL)
00945     {
00946       const char* str = env->GetStringUTFChars(jstr, NULL);
00947       path =  str;
00948       env->ReleaseStringUTFChars(jstr, str);
00949 
00950       status = true;
00951     }
00952 
00953   vm->DetachCurrentThread();
00954 
00955   return status;
00956 }
