00971 bool MAINPROCANDROID::GetAbsolutePath(struct android_app* app, XSTRING& path)
00972 {
00973   JNIEnv* env;
00974   JavaVM* vm     = app->activity->vm;
00975   bool    status = false;
00976 
00977   vm->AttachCurrentThread(&env, NULL);
00978 
00979   jclass    activityclass   = env->GetObjectClass(app->activity->clazz);
00980   jmethodID getfilesdir     = env->GetMethodID(activityclass, "getFilesDir", "()Ljava/io/File;");
00981   jobject   file            = env->CallObjectMethod(app->activity->clazz, getfilesdir);
00982   jclass    fileclass       = env->FindClass("java/io/File");
00983   jmethodID getabsolutepath = env->GetMethodID(fileclass, "getAbsolutePath", "()Ljava/lang/String;");
00984 
00985   jstring jpath = (jstring)env->CallObjectMethod(file, getabsolutepath);
00986   if(jpath != NULL)
00987     {
00988       const char* str = env->GetStringUTFChars(jpath, NULL);
00989       path =  str;
00990       env->ReleaseStringUTFChars(jpath, str);
00991       status = true;
00992     }
00993 
00994   vm->DetachCurrentThread();
00995 
00996   return status;
00997 }
