01224 bool MAINPROCANDROID::OverturnAssetsToExternalLocation(XPATH& origin, XPATH& datapath, XPATH& target)
01225 {
01226   XFILEUNZIP* unzip = new XFILEUNZIP();
01227   if(!unzip) return false;
01228 
01229   XSTRING  assetsstring;
01230   bool     status  = false;
01231 
01232   assetsstring = __L("assets");
01233 
01234   XDIR* GEN_XFACTORY_CREATE(xdir, Create_Dir())
01235   if(xdir)
01236     {
01237       status = unzip->Open(origin);
01238 
01239       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Android Resources] ZIP File: %s"), origin.Get());
01240 
01241       if(status)
01242         {
01243           XFILECMPZIP* filecmp;
01244           int          index = 0;
01245 
01246           status = true;
01247 
01248           do{
01249               filecmp = unzip->GotoFile(index);
01250               if(filecmp)
01251                 {
01252                   XPATH targetpath;
01253 
01254                   targetpath  = datapath;
01255                   targetpath.Slash_Add();
01256                   targetpath += filecmp->GetName();
01257 
01258                   targetpath.SetOnlyPath();
01259 
01260                   int position=targetpath.Find(assetsstring,true);
01261                   if(position!=-1)
01262                     {
01263                       XPATH targetfile;
01264                       XPATH namefile;
01265 
01266                       namefile = filecmp->GetName();
01267                       namefile.SetOnlyNamefileExt();
01268 
01269                       //targetpath.DeleteCharacters(position, assetsstring.GetSize()+1);
01270 
01271                       targetfile  = targetpath;
01272                       targetfile += namefile.Get();
01273 
01274                       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01275                       if(xfile)
01276                         {
01277                           //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Android Resources] ZIP descompress File: %s"), targetfile.Get());
01278 
01279                           if(!xfile->Exist(targetfile.Get()))
01280                             {
01281                               xdir->Make(targetpath.Get(), true);
01282                               status = unzip->DecompressFile(filecmp,targetpath,namefile.Get());
01283                             }
01284 
01285                           GEN_XFACTORY.Delete_File(xfile);
01286                         }
01287                     }
01288 
01289                   delete filecmp;
01290 
01291                   index++;
01292 
01293                 } else break;
01294 
01295             } while(status);
01296 
01297           unzip->Close();
01298 
01299         }
01300 
01301       GEN_XFACTORY.Delete_Dir(xdir);
01302     }
01303 
01304   delete unzip;
01305 
01306   return status;
01307 }
