01321 bool MAINPROCANDROID::AssetsDir_CreateAll(XPATH& origin)
01322 {
01323   AssetsDir_DeleteAll();
01324 
01325   XPATH       targetpath;
01326   XFILEUNZIP* unzip   = new XFILEUNZIP();
01327   XSTRING     assetsstring;
01328   bool        status  = false;
01329 
01330   if(!unzip) return false;
01331 
01332   assetsstring = __L("assets");
01333 
01334   status = unzip->Open(origin);
01335   if(status)
01336     {
01337       XFILECMPZIP* filecmp;
01338       int          index = 0;
01339 
01340       status = true;
01341 
01342       do{ filecmp = unzip->GotoFile(index);
01343           if(filecmp)
01344             {
01345               targetpath.Empty();
01346               
01347               targetpath += filecmp->GetName();
01348 
01349               targetpath.SetOnlyPath();
01350 
01351               int position=targetpath.Find(assetsstring,true);
01352               if(position!=-1)
01353                 {   
01354                   bool found = false;
01355              
01356                   targetpath.DeleteCharacters(position, assetsstring.GetSize()+1);   
01357 
01358                   for(int c=0; c<assetsdirs.GetSize(); c++)
01359                     {
01360                       XPATH* xpath;
01361 
01362                       xpath = assetsdirs.Get(c);
01363                       if(!xpath->Compare(targetpath)) 
01364                         {
01365                           found = true;
01366                           break;                               
01367                         }
01368                     }               
01369 
01370                   if(!found)
01371                     {
01372                       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Assets XPATH: %s"), targetpath.Get());
01373                       
01374                       AssetsDir_Add(&targetpath);                                                                                                  
01375                     }
01376                 } 
01377 
01378               index++;
01379 
01380               delete filecmp;
01381 
01382             } else break;
01383 
01384         } while(status);
01385 
01386       unzip->Close();        
01387     }
01388       
01389   delete unzip;
01390 
01391   return status;
01392 }
