00680 bool MAINPROCWINDOWS::IsRunningAsService()
00681 {
00682  // return IsRunningAsService(APPMODE_SERVICE_NAME);
00683 
00684   SERVICE_STATUS_PROCESS ssstatus;
00685   DWORD                  bytesneeded;
00686   SC_HANDLE              schscmanager;
00687 
00688   schscmanager = OpenSCManager( NULL,                              // local computer
00689                                 NULL                  ,            // servicesActive database
00690                                 SC_MANAGER_ALL_ACCESS  );          // full access rights
00691   if(!schscmanager) return false;
00692 
00693   // Get a handle to the service.
00694   SC_HANDLE schservice = OpenService( schscmanager          ,      // SCM database
00695                                       APPMODE_SERVICE_NAME  ,      // name of service
00696                                       SERVICE_ALL_ACCESS);         // full access
00697 
00698   if(!schservice)
00699     {
00700       CloseServiceHandle(schscmanager);
00701       return false;
00702     }
00703 
00704   // Check the status in case the service is not stopped.
00705 
00706   if(!QueryServiceStatusEx( schservice,                             // handle to service
00707                             SC_STATUS_PROCESS_INFO,                 // information level
00708                             (LPBYTE) &ssstatus,                     // address of structure
00709                             sizeof(SERVICE_STATUS_PROCESS),         // size of structure
00710                             &bytesneeded ) )                        // size needed if buffer is too small
00711     {
00712       CloseServiceHandle(schservice);
00713       CloseServiceHandle(schscmanager);
00714       return false;
00715     }
00716 
00717   DWORD processid = GetCurrentProcessId();
00718   if(processid == ssstatus.dwProcessId)
00719     {
00720       CloseServiceHandle(schservice);
00721       CloseServiceHandle(schscmanager);
00722 
00723       return true;
00724     }
00725 
00726   CloseServiceHandle(schservice);
00727   CloseServiceHandle(schscmanager);
00728 
00729   if((!ssstatus.dwProcessId) && (ssstatus.dwCurrentState == SERVICE_START_PENDING)) return true;
00730 
00731   return false;
00732 
00733 }
