00873 void WINDOWSSERVICE::On_Stop()
00874 {
00875   #ifdef APP_ACTIVE
00876   if(mainprocwindows.GetAppMain())
00877     {
00878       if(mainprocwindows.GetAppMain()->GetApplication())
00879         {
00880           if(mainprocwindows.GetAppMain()->GetApplication()->GetExitType() == APPBASE_EXITTYPE_UNKNOWN)  
00881             {
00882               mainprocwindows.GetAppMain()->GetApplication()->SetExitType(APPBASE_EXITTYPE_BY_USER);            
00883             }
00884         }
00885     }
00886   #endif
00887 
00888   if(event_stopped)
00889     {
00890       while(WaitForSingleObject(event_stopped, 0) != WAIT_OBJECT_0)
00891         {
00892           Sleep(5*1000);          
00893         }
00894     }
00895   
00896   if(MustRestartService())
00897     {
00898      
00899     }
00900 }
