01016 
01017   if(!service) return false;
01018 
01019   #ifdef APP_ACTIVE
01020   if(!mainprocwindows.Ini(&GEN_appmain, APPBASE_APPLICATIONMODE_TYPE_SERVICE))
01021   #else
01022   if(!mainprocwindows.Ini())
01023   #endif
01024     {
01025       status = 1;
01026     }
01027 
01028   if(!status)
01029     {
01030       while(1)
01031         {
01032           if(!mainprocwindows.Update())
01033             {
01034               userstop = true;
01035               break;
01036             }
01037         }
01038     }
01039 
01040   
01041   APPBASE_EXITTYPE exittype = APPBASE_EXITTYPE_UNKNOWN;
01042 
01043   #ifdef APP_ACTIVE  
01044   exittype =  mainprocwindows.GetAppMain()->GetApplication()->GetExitType();
01045   #endif
01046   
01047   mainprocwindows.GetXPathExec()->Empty();
01048   mainprocwindows.End();
01049 
01050 
01051   if(exittype == APPBASE_EXITTYPE_BY_SERVICERELOAD)
01052     {
01053       service->SetMustRestartService(true);
01054     }
01055 
01056   if(service->GetHandleStoppedEvent()) 
01057     {
01058       SetEvent(service->GetHandleStoppedEvent());
01059     }
01060 
01061   if(userstop) 
01062     {
01063       service->Stop();
01064     }
01065 
01066   return ERROR_SUCCESS;
01067 }
