01205 int WINAPI WinMain(HINSTANCE hinstance, HINSTANCE hprevinst, LPSTR cmdline, int cmdshow)
01206 {
01207   XMEMORY_CONTROL_ACTIVATED
01208 
01209   int      nargs        = 0;
01210   LPWSTR*  ptrpathexec  = CommandLineToArgvW(__L(""), &nargs);
01211   XSTRING  xpathexecutable;
01212   XSTRING  cmdlinestr   = cmdline;
01213 
01214   mainprocwindows.SetHandle((void*)hinstance);
01215 
01216   for(int c=0; c<nargs; c++)
01217     {
01218       xpathexecutable += ptrpathexec[c];
01219       if(c != nargs-1)  xpathexecutable += __L(" ");
01220     }
01221 
01222   mainprocwindows.GetXPathExec()->Set(xpathexecutable);
01223 
01224   mainprocwindows.CreateParams(cmdlinestr.Get());
01225   cmdlinestr.Empty();
01226   xpathexecutable.Empty(); 
01227 
01228   if(!mainprocwindows.IsRunningAsService())
01229     {
01230       mainprocwindows.MainLoop();
01231     }
01232     else
01233     {
01234       WINDOWSSERVICE service(APPMODE_SERVICE_NAME);
01235       if(!WINDOWSSERVICE::Run(service))
01236         {
01237 
01238 
01239         }
01240     }
01241 
01242   #ifdef APP_ACTIVE
01243   if(mainprocwindows.GetAppMain())
01244     {
01245       if(mainprocwindows.GetAppMain()->GetApplication())
01246         {
01247           return mainprocwindows.GetAppMain()->GetApplication()->GetExitType();
01248         }
01249     }
01250   #endif
01251 
01252   return 0;
01253 }
