01272 BOOL WINAPI DllMain(HINSTANCE hinstDLL, XDWORD fdwReason,LPVOID lpvReserved)
01273 {
01274   switch(fdwReason)
01275     {
01276       case DLL_PROCESS_ATTACH : { XMEMORY_CONTROL_ACTIVATED                                  
