01278                                   XPATH xpathexecutable;
01279 
01280                                   xpathexecutable.AdjustSize(_MAXPATH);
01281                                   GetModuleFileName(hinstDLL, xpathexecutable.Get(), xpathexecutable.GetSize());
01282                                   xpathexecutable.AdjustSize();
01283 
01284                                   mainprocwindows.GetXPathExec()->Set(xpathexecutable);
01285                                   xpathexecutable.Empty();
01286                                   
01287                                   #ifdef APP_ACTIVE
