01291                                   #endif
01292                                 }
01293                                 break;
01294 
01295       case DLL_THREAD_ATTACH  : break;
01296 
01297       case DLL_THREAD_DETACH  : break;
01298 
01299       case DLL_PROCESS_DETACH : mainprocwindows.GetXPathExec()->Empty();
01300                                 mainprocwindows.End();
01301                                 break;
01302     }
01303 
01304   return true;
