01349 void MAINPROCWINDOWSSTACKWALKER::OnCallstackEntry(CallstackEntryType eType, CallstackEntry& entry)
01350 {
01351   XSTRING modulename;
01352   XSTRING linefilename;
01353   XSTRING name;
01354 
01355   if((eType != lastEntry) && (entry.offset != 0))
01356     {
01357       if(entry.name[0]         == 0)  strcpy_s(entry.name, "(function-name not available)");
01358       if(entry.undName[0]      != 0)  strcpy_s(entry.name, entry.undName);
01359       if(entry.undFullName[0]  != 0)  strcpy_s(entry.name, entry.undFullName);
01360       if(entry.lineFileName[0] == 0)
01361         {
01362           strcpy_s(entry.lineFileName, "(filename not available)");
01363           if (entry.moduleName[0] == 0) strcpy_s(entry.moduleName, "(module-name not available)");
01364         }
01365 
01366       modulename   = entry.moduleName;
01367       linefilename = entry.lineFileName;
01368       name         = entry.name;
01369 
01370       Exception_Printf(true, NULL, __L("%08X  %-48s (%6d) %-64s"), (LPVOID) entry.offset, name.Get(), entry.lineNumber, linefilename.Get());
01371     }
01372 }
