01383 
01395 BOOL Exception_ConsoleHandler(DWORD fdwctrltype)
01396 {
01397   #ifdef APP_ACTIVE
01398   APPBASE* app = NULL;
01399   if(mainprocwindows.GetAppMain()) app = mainprocwindows.GetAppMain()->GetApplication();
01400   #endif
01401 
01402   switch(fdwctrltype)
01403     {
01404       case CTRL_BREAK_EVENT     : Exception_Printf(false, __L("EVENT"), __L("Ctrl-Break event."));
01405                                   #ifdef APP_ACTIVE
01406                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_USER);
01407                                   #endif
01408                                   return FALSE;
01409 
01410       case CTRL_C_EVENT         : Exception_Printf(false, __L("EVENT"), __L("Ctrl-C event."));
01411                                   #ifdef APP_ACTIVE
01412                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_USER);
01413                                   #endif
01414                                   return FALSE;
01415 
01416       case CTRL_CLOSE_EVENT     :
01417                                   #ifdef APP_ACTIVE
01418                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_USER);
01419                                   #endif
01420                                   FreeConsole();         // Detachxconsole
01421                                   break;
01422 
01423       case CTRL_LOGOFF_EVENT    : Exception_Printf(false, __L("EVENT"), __L("Ctrl-Logoff event."));
01424                                   #ifdef APP_ACTIVE
01425                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_SHUTDOWN);
01426                                   #endif
01427                                   FreeConsole();         // Detachxconsole
01428                                   ExitThread(0);         // Prevent closing.
01429                                   break;
01430 
01431       case CTRL_SHUTDOWN_EVENT  : Exception_Printf(false, __L("EVENT"), __L("Ctrl-Shutdown event."));
01432                                   #ifdef APP_ACTIVE
01433                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_SHUTDOWN);
01434                                   #endif
01435                                   FreeConsole();         // Detachxconsole
01436                                   ExitThread(0);         // Prevent closing.
01437                                   break;
01438 
01439                       default   : return FALSE;
01440     }
01441 
01442   return FALSE;
01443 }
