01445 
01458 int Exception_Filter(XDWORD code, struct _EXCEPTION_POINTERS* ep)
01459 {
01460   XSTRING exception;
01461   XSTRING description;
01462   XSTRING string;
01463 
01464   allexceptiontext = new XSTRING();
01465   if(!allexceptiontext) return 0;
01466 
01467   APPBASE* app = NULL;
01468   #ifdef APP_ACTIVE
01469   if(mainprocwindows.GetAppMain()) app = mainprocwindows.GetAppMain()->GetApplication();
01470   #endif
01471 
01472 
01473   switch(code)
01474     {
01475       case EXCEPTION_ACCESS_VIOLATION         : exception   = __L("ACCESS VIOLATION");
01476                                                 description = __L("Read from or write to a virtual address for which it does not have access.");
01477                                                 break;
01478 
01479       case EXCEPTION_ARRAY_BOUNDS_EXCEEDED    : exception   = __L("ARRAY BOUNDS EXCEEDED");
01480                                                 description = __L("Access an array element that is out of bounds");
01481                                                 break;
01482 
01483       case EXCEPTION_DATATYPE_MISALIGNMENT    : exception   = __L("DATATYPE MISALIGNMENT");
01484                                                 description = __L("Read or write data that is misaligned on hardware that does not provide alignment.");
01485                                                 break;
01486 
01487       case EXCEPTION_FLT_DENORMAL_OPERAND     : exception   = __L("FLT DENORMAL OPERAND]");
01488                                                 description = __L("One of the operands in a floating point operation is denormal.");
01489                                                 break;
01490 
01491       case EXCEPTION_FLT_DIVIDE_BY_ZERO       : exception   = __L("FLT DIVIDE BY ZERO");
01492                                                 description = __L("Divide a floating point value by a floating point divisor of 0 (zero).");
01493                                                 break;
01494 
01495       case EXCEPTION_FLT_INEXACT_RESULT       : exception   = __L("FLT INEXACT RESULT");
01496                                                 description = __L("A floating point operation cannot be represented exactly as a decimal fraction.");
01497                                                 break;
01498 
01499       case EXCEPTION_FLT_INVALID_OPERATION    : exception   = __L("FLT INVALID OPERATION");
01500                                                 description = __L("A floating point exception (not defined).");
01501                                                 break;
01502 
01503       case EXCEPTION_FLT_OVERFLOW             : exception   = __L("FLT OVERFLOW");
01504                                                 description = __L("The exponent of a floating point operation is greater than the magnitude allowed by the corresponding type.");
01505                                                 break;
01506 
01507       case EXCEPTION_FLT_STACK_CHECK          : exception   = __L("FLT STACK CHECK");
01508                                                 description = __L("The stack has overflowed or underflowed, because of a floating point operation.");
01509                                                 break;
01510 
01511       case EXCEPTION_FLT_UNDERFLOW            : exception   = __L("FLT UNDERFLOW");
01512                                                 description = __L("The exponent of a floating point operation is less than the magnitude allowed by the corresponding type.");
01513                                                 break;
01514 
01515       case EXCEPTION_GUARD_PAGE               : exception   = __L("GUARD PAGE");
01516                                                 description = __L("Accessed memory allocated with the PAGE GUARD modifier.");
01517                                                 break;
01518 
01519       case EXCEPTION_ILLEGAL_INSTRUCTION      : exception   = __L("ILLEGAL INSTRUCTION");
01520                                                 description = __L("Tries to execute an invalid instruction.");
01521                                                 break;
01522 
01523       case EXCEPTION_IN_PAGE_ERROR            : exception   = __L("IN PAGE ERROR");
01524                                                 description = __L("Tries to access a page that is not present, and the system is unable to load the page.");
01525                                                 break;
01526 
01527       case EXCEPTION_INT_DIVIDE_BY_ZERO       : exception   = __L("INT DIVIDE BY ZERO");
01528                                                 description = __L("Divide an integer value by an integer divisor of 0 (zero).");
01529                                                 break;
01530 
01531       case EXCEPTION_INT_OVERFLOW             : exception   = __L("INT OVERFLOW");
01532                                                 description = __L("The result of an integer operation creates a value that is too large to be held by the destination register.");
01533                                                 break;
01534 
01535       case EXCEPTION_INVALID_DISPOSITION      : exception   = __L("INVALID DISPOSITION");
01536                                                 description = __L("An exception handler returns an invalid disposition to the exception dispatcher.");
01537                                                 break;
01538 
01539       case EXCEPTION_INVALID_HANDLE           : exception   = __L("INVALID HANDLE") ;
01540                                                 description = __L("The thread used a handle to a kernel object that was invalid (probably because it had been closed).");
01541                                                 break;
01542 
01543       case EXCEPTION_NONCONTINUABLE_EXCEPTION : exception   = __L("NONCONTINUABLE EXCEPTION");
01544                                                 description = __L("The thread attempts to continue execution after a non-continuable exception occurs.");
01545                                                 break;
01546 
01547       case EXCEPTION_PRIV_INSTRUCTION         : exception   = __L("PRIV INSTRUCTION");
01548                                                 description = __L("Execute an instruction with an operation that is not allowed in the current computer mode.");
01549                                                 break;
01550 
01551       case EXCEPTION_STACK_OVERFLOW           : exception   = __L("STACK OVERFLOW");
01552                                                 description = __L("Stack overflow");
01553                                                 break;
01554 
01555       case EXCEPTION_BREAKPOINT               : //Exception_Printf(__L("[EXCEPTION_BREAKPOINT]")              , __L("A breakpoint is encountered"));
01556       case EXCEPTION_SINGLE_STEP              : //Exception_Printf(__L("[EXCEPTION_SINGLE_STEP]")             , __L("A trace trap or other single instruction mechanism signals that one instruction is executed."));
01557       case STATUS_UNWIND_CONSOLIDATE          : return EXCEPTION_CONTINUE_SEARCH;
01558                                                 break;
01559 
01560                                       default : return EXCEPTION_EXECUTE_HANDLER;
01561     }
01562 
01563   Exception_Printf(true, __L("EXCEPTION"), __L("%s: %s"), exception.Get(), description.Get());
01564 
01565   #ifdef APP_ACTIVE
01566   if(app)
01567     {
01568       if(app->GetTimerGlobal())
01569         {
01570           app->GetTimerGlobal()->GetMeasureString(string, true);
01571           Exception_Printf(false, NULL, __L("Time working: %s."), string.Get());
01572         }
01573     }
01574   #endif
01575 
01576   switch(code)
01577     {
01578       case EXCEPTION_ACCESS_VIOLATION         :
01579       case EXCEPTION_ARRAY_BOUNDS_EXCEEDED    :
01580       case EXCEPTION_DATATYPE_MISALIGNMENT    :
01581       case EXCEPTION_FLT_DENORMAL_OPERAND     :
01582       case EXCEPTION_FLT_DIVIDE_BY_ZERO       :
01583       case EXCEPTION_FLT_INEXACT_RESULT       :
01584       case EXCEPTION_FLT_INVALID_OPERATION    :
01585       case EXCEPTION_FLT_OVERFLOW             :
01586       case EXCEPTION_FLT_STACK_CHECK          :
01587       case EXCEPTION_FLT_UNDERFLOW            :
01588       case EXCEPTION_GUARD_PAGE               :
01589       case EXCEPTION_ILLEGAL_INSTRUCTION      :
01590       case EXCEPTION_IN_PAGE_ERROR            :
01591       case EXCEPTION_INT_DIVIDE_BY_ZERO       :
01592       case EXCEPTION_INT_OVERFLOW             :
01593       case EXCEPTION_INVALID_DISPOSITION      :
01594       case EXCEPTION_INVALID_HANDLE           :
01595       case EXCEPTION_NONCONTINUABLE_EXCEPTION :
01596       case EXCEPTION_PRIV_INSTRUCTION         :
01597       case EXCEPTION_STACK_OVERFLOW           : { 
01598                                                   #ifdef WINDOWS_STACKWALKER_ACTIVE
01599 
01600                                                   MAINPROCWINDOWSSTACKWALKER stackwalker;
01601                                                   stackwalker.ShowCallstack(GetCurrentThread(), ep->ContextRecord);
01602 
01603 
01604                                                   string.Format(__L("EXCEPTION %s"), exception.Get());
01605 
01606                                                   #ifdef DIO_ALERTS_ACTIVE
01607                                                   DIOALERT* alert = GEN_DIOALERTS.CreateAlert(DIOALERTLEVEL_DANGER, string.Get(), allexceptiontext->Get());
01608                                                   if(alert)
01609                                                     {
01610                                                       GEN_DIOALERTS.Send(DIOALERTSSENDER_ALL, DIOALERTS_CONDITIONS_ID_GENINTERN_EXCEPTION, alert);
01611                                                       delete alert;
01612                                                     }
01613                                                   #endif
01614 
01615                                                   #ifdef APP_ACTIVE
01616                                                   if(app) exit(APPBASE_EXITTYPE_BY_SERIOUSERROR);
01617                                                   #endif
01618 
01619                                                   #endif 
01620                                                 }
01621                                                 break;
01622     }
01623 
01624   if(allexceptiontext)
01625     {
01626       delete allexceptiontext;
01627       allexceptiontext = NULL;
01628     }
01629 
01630   return EXCEPTION_EXECUTE_HANDLER;
01631 }
